import React, { Component } from 'react';
import factory from '../ethereum/factory';
import { Card, CardGroup, Button } from 'semantic-ui-react';
import Layout from'../components/Layout';
import { Link } from '../routes';
import Campaign from '../ethereum/campaign';

class CampaignIndex extends Component {
    static async getInitialProps() {
        const campaigns = await factory.methods.getDeployedCampaigns().call();
        return { campaigns: campaigns };
    }

    async renderCampaigns() {
        const items = this.props.campaigns.map(async address => {
            const campaign = Campaign(address);
            const description = await campaign.methods.campaignDescription().call();

            return {
                header: address,
                meta: description,
                description: (
                    <Link route={`/campaign/${address}`}>
                        <a>View Campaign</a>
                    </Link>
                ),
                fluid: true
            }
        });

        return <Card.Group items={items} />;
    }

    render() {
        return (
            <Layout>
                <div>
                    <h3>Open Campaigns</h3>
                    <Link route="/campaign/new">
                        <a>
                            <Button 
                                floated="right"
                                content="Create Campaign" 
                                icon="add" 
                                primary
                            />
                        </a>    
                    </Link>

                    {this.renderCampaigns()}
                </div>
            </Layout>
        );
    }
}

export default CampaignIndex;
